  ];

  const len = [select1.length, select2.length, select3.length, select4.length];

  const list = ["인간과 신앙", "인간과 사상", "인간과 사회", "인간과 과학"];

  const c_select = [
    94 +
      (100 / maxSelect[0]) *
        (maxSelect[0] -
          select1.reduce((total, currentRow) => total + currentRow[1], 0)),
    94 +
      (100 / maxSelect[1]) *
        (maxSelect[1] -
          select2.reduce((total, currentRow) => total + currentRow[1], 0)),
    94 +
      (100 / maxSelect[2]) *
        (maxSelect[2] -
          select3.reduce((total, currentRow) => total + currentRow[1], 0)),
    94 +
      (100 / maxSelect[3]) *
        (maxSelect[3] -
          select4.reduce((total, currentRow) => total + currentRow[1], 0)),
  ];

  return (
    <>
      <ProgressBar>
        <Progress width={100 - (availableItem * 100) / maxItem} bgColor={bg} />
      </ProgressBar>
      <BigTitles>
        <FontAwesomeIcon icon={faComment} style={{ color: "#FF6262" }} /> 나의
        교과과정과 부전공을 선택하세요
      </BigTitles>
      <BigBox>
        <LeftBox>
          {curr.map((item, index) => (
            <Title>
              <Icon>
                <FontAwesomeIcon
                  icon={faCheck}
                  style={{ color: "#FF6262", fontSize: "1.7rem" }}
                />
              </Icon>
              <TitleText>
                {list[index]}
                <SmallBox>
                  <XSmaillBox>
                    {item.lectures &&
                      item.lectures.map((lecture) => (
                        <SelectContainer
                          {...lecture}
                          setSelect={setSelect[index]}
                          select={select[index]}
                          ex_select={ex_select[index]}
                          setSelectEX={setSelectEX[index]}
                        />
                      ))}
                  </XSmaillBox>
                </SmallBox>
              </TitleText>
            </Title>
          ))}
        </LeftBox>
        <RightBox>
          <TotalBar>
            내 학점 채우기 현황
            <br />
            <FirstBar>
              <Bar>
                <BarText>전체</BarText>

                <MiniBar>
                  <Mini
                    width={100 - (availableItem * 100) / maxItem}
                    bgColor={bg}
                  />
                </MiniBar>
              </Bar>

              <Bar>
                <BarText>컴퓨터공학</BarText>

                <MiniBar>
                  <Mini
                    width={100 - (availableItem * 100) / maxItem}
                    bgColor={bg}
                  />
                </MiniBar>
              </Bar>

              <Bar>
                <BarText>영미영문</BarText>

                <MiniBar>
                  <Mini
                    width={100 - (availableItem * 100) / maxItem}
                    bgColor={bg}
                  />
                </MiniBar>
              </Bar>

              <Bar>
                <BarText>영어강의</BarText>

                <MiniBar>
                  <Mini
                    width={100 - (availableItem * 100) / maxItem}
                    bgColor={bg}
                  />
                </MiniBar>
              </Bar>
            </FirstBar>
          </TotalBar>

          <SelectBar>
            <SecondBar>
              {curr.map((item, index) => (
                <Bar>
                  <BarText>{list[index]}</BarText>
                  <MiniBar>
                    <Mini
                      width={
                        (select[index].reduce(
                          (total, currentRow) => total + currentRow[1],
                          0
                        ) *
                          100) /
                        maxSelect[index]
                      }
                      bgColor={`rgb(255, ${c_select[index]}, ${c_select[index]})`}
                      ex_width={
                        (ex_select[index].reduce(
                          (total, currentRow) => total + currentRow[1],
                          0
                        ) *
                          100) /
                        maxSelect[index]
                      }
                      ex_bg={`rgb(255, ${
                        94 +
                        (100 / maxSelect[index]) *
                          (maxSelect[index] -
                            ex_select[index].reduce(
                              (total, currentRow) => total + currentRow[1],
                              0
                            ))
                      }, ${
                        94 +
                        (100 / maxSelect[index]) *
                          (maxSelect[index] -
                            ex_select[index].reduce(
                              (total, currentRow) => total + currentRow[1],
                              0
                            ))
                      })`}
                    />
                  </MiniBar>
                  <PointDisplay>
                    (
                    {select[index].reduce(
                      (total, currentRow) => total + currentRow[1],
                      0
                    )}
                    /{maxSelect[index]})
                  </PointDisplay>
                </Bar>
              ))}
            </SecondBar>
          </SelectBar>
          <Save>임시저장</Save>
          <Next>다음으로</Next>
        </RightBox>
      </BigBox>
    </>
  );
};

