import React from "react";
import styled from "styled-components";
import Header from "./Header";
import "./fonts/Font.css";
import PageTitle from "./PageTitle";
import BarList from "./StatusBar";

const CurrentStatus = () => {
  return (
    <>
      <Header />
      <StatusContainer>
        <PageTitle
          text={{
            bold: "내 학점 채우기 현황은",
            normal: " 다음과 같아요",
          }}
        />
        <StatusMain>
          <StatusPie>
            <PieTitle>전체 학점</PieTitle>
            <Pie></Pie>
            <CreditStatus>104/130</CreditStatus>
          </StatusPie>
          <StatusBar>
            <BarList barli={{ title: "영미영문", credits: "30" }} />
          </StatusBar>
        </StatusMain>
        <StatusSubmit></StatusSubmit>
      </StatusContainer>
    </>
  );
};

const StatusContainer = styled.div`
  width: 70%;
  height: 100vh;
  margin: 2em auto;
  font-family: "Noto Sans KR";
  font-weight: 400;
  text-align: center;
  font-size: 1em;
`;
const StatusMain = styled.div`
  width: 100%;
  font-family: "BMJUA";
  font-size: 1.4em;
  font-weight: normal;
  display: flex;
  justify-content: space-between;
`;
const StatusPie = styled.div`
  width: 38%;
  height: 17em;
  border-radius: 50px 0px;
  border: 0.08em solid #cdcdcd;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
`;
const PieTitle = styled.div`
  margin-top: 1em;
`;
const Pie = styled.div`
  width: 10em;
  height: 10em;
  border-radius: 50%;
  background-color: gray;
`;
const CreditStatus = styled.div`
  margin-bottom: 1em;
`;
const StatusBar = styled.div`
  width: 57%;
  border-radius: 0px 50px;
  border: 0.08em solid #cdcdcd;
`;
const StatusSubmit = styled.div``;

export default CurrentStatus;
