                      ex_bg={`rgb(255, ${
                        94 +
                        (100 / maxSelect[index]) *
                          (maxSelect[index] -
                            ex_select[index].reduce(
                              (total, currentRow) => total + currentRow[1],
                              0
                            ))
                      }, ${
                        94 +
                        (100 / maxSelect[index]) *
                          (maxSelect[index] -
                            ex_select[index].reduce(
                              (total, currentRow) => total + currentRow[1],
                              0
                            ))
                      })`}
                    />
                  </MiniBar>
                  <PointDisplay>
                    (
                    {select[index].reduce(
                      (total, currentRow) => total + currentRow[1],
                      0
                    )}
                    /{maxSelect[index]})
                  </PointDisplay>
                </Bar>
              ))}
            </SecondBar>
          </SelectBar>
          <Save>임시저장</Save>
          <Next onClick={goNext}>다음으로</Next>
        </RightBox>
      </BigBox>
    </>
