import React from "react";
import styled from "styled-components";
import Header from "./Header";
import "./fonts/Font.css";

const CurrentState = () => {
  return (
    <>
      <Header />
      <StateContainer>
        <StateTitle>내 학점 채우기 현황은 다음과 같아요</StateTitle>
        <StateMain>
          <StatePie>
            <span>전체 학점</span>
            <Pie></Pie>
            <span></span>
          </StatePie>
          <StateBar></StateBar>
        </StateMain>
        <StateSubmit></StateSubmit>
      </StateContainer>
    </>
  );
};

const StateContainer = styled.div`
  width: 70%;
  height: 100vh;
  margin: 0 auto;
  font-family: "Noto Sans KR";
  font-weight: 400;
  text-align: center;
  font-size: 1em;
`;
const StateTitle = styled.div`
  font-family: "BMJUA";
  font-size: 2em;
  font-weight: normal;
`;
const StateMain = styled.div``;
const StatePie = styled.div``;
const StateBar = styled.div``;
const StateSubmit = styled.div``;

export default CurrentState;
