import { useEffect, useState, useRef } from "react";
import styled, { keyframes } from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faComment, faCheck } from "@fortawesome/free-solid-svg-icons";

const curri = ["서강인성"];

const curr = [
  {
    track: "단일전공",
    "이수 학점": 16,
  },
  {
    category_detail: "필수",
    category_point: 13,
    lectures: [
      {
        id: 2,
        title: "미적분학II",
        code: "STS2006",
        point: 3,
        eta: "",
        semester_one: 3,
        semester_two: 3,
        teamplay: 1,
        grade_recommend: 1,
        season_open: true,
        teach: false,
        advance: false,
        former: null,
        category21: 11,
        category22: 11,
        category23: 11,
        category24: null,
        tech: null,
      },
      {
        id: 3,
        title: "일반물리실험I",
        code: "PHY1101",
        point: 1,
        eta: "",
        semester_one: 3,
        semester_two: 3,
        teamplay: 1,
        grade_recommend: 1,
        season_open: true,
        teach: false,
        advance: false,
        former: null,
        category21: 11,
        category22: 11,
        category23: 11,
        category24: null,
        tech: null,
      },
      // 나머지 강의들도 포함
    ],
  },
  {
    category_detail: "선택",
    category_point: 3,
    lectures: [
      {
        id: 7,
        title: "집합론",
        code: "MAT2010",
        point: 3,
        eta: "",
        semester_one: 3,
        semester_two: 3,
        teamplay: 1,
        grade_recommend: 1,
        season_open: true,
        teach: false,
        advance: false,
        former: null,
        category21: 12,
        category22: 12,
        category23: 12,
        category24: null,
        tech: null,
      },
      {
        id: 8,
        title: "선형대수학",
        code: "MAT2110",
        point: 3,
        eta: "",
        semester_one: 3,
        semester_two: 3,
        teamplay: 1,
        grade_recommend: 1,
        season_open: true,
        teach: false,
        advance: false,
        former: null,
        category21: 12,
        category22: 12,
        category23: 12,
        category24: null,
        tech: null,
      },
      // 나머지 강의들도 포함
    ],
  },
];

const SelectContainer = ({
  id,
  title,
  setSelect,
  select,
  ex_select,
  setSelectEX,
}) => {
  const [isClicked, setisClicked] = useState(false);

  const onClick = () => {
    setSelectEX(select);

    setisClicked(!isClicked);
    if (isClicked) {
      const filtered = select.filter((item) => item != id);
      setSelect(filtered);
    } else {
      setSelect([...select, id]);
    }
  };

  return (
    <>
      <SelectBox onClick={onClick} isClicked={isClicked}>
        {title}
        <HoverBox>교수: ㅇㅇㅇ<br></br>과제: 많음<br></br>성적: 깐깐함</HoverBox>
      </SelectBox>
    </>
  );
};

const Selecttech = () => {
  const maxItem = 5;
  let availableItem = 4;
  const c = 194 - (100 / maxItem) * (maxItem - availableItem);
  const bg = `rgb(255, ${c}, ${c})`;

  const maxSelect = [4, 1, 1, 3, 2];

  const [select, setSelect] = useState([]);
  const [select1, setSelect1] = useState([]);
  const [select2, setSelect2] = useState([]);
  const [select3, setSelect3] = useState([]);
  const [select4, setSelect4] = useState([]);
  const [ex_select, setSelectEX] = useState([]);
  const [ex_select1, setSelectEX1] = useState([]);
  const [ex_select2, setSelectEX2] = useState([]);
  const [ex_select3, setSelectEX3] = useState([]);
  const [ex_select4, setSelectEX4] = useState([]);

  const len = [
    select.length,
    select1.length,
    select2.length,
    select3.length,
    select4.length,
  ];

  
  const c_select = [
    94 + (100 / maxSelect[0]) * (maxSelect[0] - len[0]),
    94 + (100 / maxSelect[1]) * (maxSelect[1] - len[1]),
    94 + (100 / maxSelect[2]) * (maxSelect[2] - len[2]),
    94 + (100 / maxSelect[3]) * (maxSelect[3] - len[3]),
    94 + (100 / maxSelect[4]) * (maxSelect[4] - len[4]),
  ];

  return (
    <>
      <ProgressBar>
        <Progress width={100 - (availableItem * 100) / maxItem} bgColor={bg} />
      </ProgressBar>
      <BigTitles>
        <FontAwesomeIcon icon={faComment} style={{ color: "#FF6262" }} /> 나의
        교과과정과 부전공을 선택하세요
      </BigTitles>
      <BigBox>
        <LeftBox>
          {curr &&
            curr.map(
              (item, index) => {
                const currentSelect = `select${index + 1}`;
    const currentSetSelect = `setSelect${index + 1}`;
    const currentExSelect = `ex_select${index + 1}`;
                const currentSetSelectEX = `setSelectEX${index + 1}`;
                if (item.category_detail === undefined) {
      // item.category_detail이 undefined일 때 해당 아이템을 건너뛕니다.
      return null;
                }
                return (
                  <Title>
                    <Icon>
                      <FontAwesomeIcon
                        icon={faCheck}
                        style={{ color: "#FF6262", fontSize: "1.7rem" }}
                      />
                    </Icon>{" "}
                    <TitleText>
                      {item.category_detail}
                      <SmallBox>
                        <XSmaillBox>
                          {item.lectures &&
                            item.lectures.map((lecture) => (
                              <SelectContainer
                                {...lecture}
                                setSelect={setSelect1}
                                select={currentSetSelect}
                                ex_select={currentExSelect}
                                setSelectEX={setSelectEX1}
                              />
                            ))}
                        </XSmaillBox>
                      </SmallBox>
                    </TitleText>
                  </Title>
                );
    
              }
                
            )}
        </LeftBox>
        <RightBox>
          <TotalBar>
            내 학점 채우기 현황
            <br />
            <FirstBar>
              <Bar>
                <BarText>전체</BarText>

                <MiniBar>
                  <Mini
                    width={100 - (availableItem * 100) / maxItem}
                    bgColor={bg}
                  />
                </MiniBar>
              </Bar>

              <Bar>
                <BarText>컴퓨터공학</BarText>

                <MiniBar>
                  <Mini
                    width={100 - (availableItem * 100) / maxItem}
                    bgColor={bg}
                  />
                </MiniBar>
              </Bar>

              <Bar>
                <BarText>영미영문</BarText>

                <MiniBar>
                  <Mini
                    width={100 - (availableItem * 100) / maxItem}
                    bgColor={bg}
                  />
                </MiniBar>
              </Bar>

              <Bar>
                <BarText>영어강의</BarText>

                <MiniBar>
                  <Mini
                    width={100 - (availableItem * 100) / maxItem}
                    bgColor={bg}
                  />
                </MiniBar>
              </Bar>
            </FirstBar>
          </TotalBar>

          <SelectBar>
            <SecondBar>
              {curr &&
                curr.map(
                  (item) =>
                    item.category_detail !== undefined && (
                      <Bar>
                        <BarText>{item.category_detail}</BarText>
                        <MiniBar>
                  <Mini
                    width={(len[0] * 100) / maxSelect[0]}
                    bgColor={`rgb(255, ${c_select[0]}, ${c_select[0]})`}
                    ex_width={(ex_select.length * 100) / maxSelect[0]}
                    ex_bg={`rgb(255, ${
                      94 +
                      (100 / maxSelect[0]) * (maxSelect[0] - ex_select.length)
                    }, ${
                      94 +
                      (100 / maxSelect[0]) * (maxSelect[0] - ex_select.length)
                    })`}
                  />
                </MiniBar>
                      </Bar>
                    )
                )}
              
              <Bar>
                <BarText>서강인성</BarText>

                <MiniBar>
                  <Mini
                    width={(len[0] * 100) / maxSelect[0]}
                    bgColor={`rgb(255, ${c_select[0]}, ${c_select[0]})`}
                    ex_width={(ex_select.length * 100) / maxSelect[0]}
                    ex_bg={`rgb(255, ${
                      94 +
                      (100 / maxSelect[0]) * (maxSelect[0] - ex_select.length)
                    }, ${
                      94 +
                      (100 / maxSelect[0]) * (maxSelect[0] - ex_select.length)
                    })`}
                  />
                </MiniBar>
              </Bar>
              <Bar>
                <BarText>글쓰기</BarText>

                <MiniBar>
                  <Mini
                    width={(len[1] * 100) / maxSelect[1]}
                    bgColor={`rgb(255, ${c_select[1]}, ${c_select[1]})`}
                    ex_width={(ex_select1.length * 100) / maxSelect[1]}
                    ex_bg={`rgb(255, ${
                      94 +
                      (100 / maxSelect[1]) * (maxSelect[1] - ex_select1.length)
                    }, ${
                      94 +
                      (100 / maxSelect[1]) * (maxSelect[1] - ex_select1.length)
                    })`}
                  />
                </MiniBar>
              </Bar>
              <Bar>
                <BarText>글로벌영어</BarText>

                <MiniBar>
                  <Mini
                    width={(len[2] * 100) / maxSelect[2]}
                    bgColor={`rgb(255, ${c_select[2]}, ${c_select[2]})`}
                    ex_width={(ex_select2.length * 100) / maxSelect[2]}
                    ex_bg={`rgb(255, ${
                      94 +
                      (100 / maxSelect[2]) * (maxSelect[2] - ex_select2.length)
                    }, ${
                      94 +
                      (100 / maxSelect[2]) * (maxSelect[2] - ex_select2.length)
                    })`}
                  />
                </MiniBar>
              </Bar>
              <Bar>
                <BarText>전공진로탐색</BarText>

                <MiniBar>
                  <Mini
                    width={(len[3] * 100) / maxSelect[3]}
                    bgColor={`rgb(255, ${c_select[3]}, ${c_select[3]})`}
                    ex_width={(ex_select3.length * 100) / maxSelect[3]}
                    ex_bg={`rgb(255, ${
                      94 +
                      (100 / maxSelect[3]) * (maxSelect[3] - ex_select3.length)
                    }, ${
                      94 +
                      (100 / maxSelect[3]) * (maxSelect[3] - ex_select3.length)
                    })`}
                  />
                </MiniBar>
              </Bar>
              <Bar>
                <BarText>소프트웨어</BarText>

                <MiniBar>
                  <Mini
                    width={(len[4] * 100) / maxSelect[4]}
                    bgColor={`rgb(255, ${c_select[4]}, ${c_select[4]})`}
                    ex_width={(ex_select4.length * 100) / maxSelect[4]}
                    ex_bg={`rgb(255, ${
                      94 +
                      (100 / maxSelect[4]) * (maxSelect[4] - ex_select4.length)
                    }, ${
                      94 +
                      (100 / maxSelect[4]) * (maxSelect[4] - ex_select4.length)
                    })`}
                  />
                </MiniBar>
              </Bar>
            </SecondBar>
          </SelectBar>
          <Save>임시저장</Save>
          <Next>다음으로</Next>
        </RightBox>
      </BigBox>
    </>
  );
};

const ProgressBar = styled.div`
  width: 60%;
  height: 20px;
  background-color: #f5f5f5;
  border-radius: 20px;

  overflow: hidden;
  margin: 2% auto;
`;

const Progress = styled.div`
  width: ${(props) => props.width}%;
  height: 30px;
  padding: 0;
  text-align: center;
  background-color: ${(props) => props.bgColor};
  color: #111;
`;

const BigTitles = styled.div`
  font-size: 1.8rem;
  text-align: center;
  margin-bottom: 5%;
  margin-top: 5%;
`;

const BigBox = styled.div`
  width: 60%;
  margin: 0 auto;
  display: flex;
`;

const LeftBox = styled.div`
  width: 70%;
`;

const RightBox = styled.div`
  width: 30%;
`;

const Title = styled.div`
  display: flex;
  margin-bottom: 1%;
`;

const Icon = styled.div`
  margin-right: 2%;
`;

const TitleText = styled.div`
  font-size: 1.3rem;
  background-color: white;
  margin-bottom: 2%;
  width: 100%;
`;

const SmallBox = styled.div`
  width: auto%;
  margin-top: 3%;
  positon :relative;
`;
const XSmaillBox = styled.div``;
const SelectBox = styled.button`
  font-size: 0.9rem;
  margin-right: 1%;
  margin-bottom: 2%;

  padding: 1% 2%;

  border-radius: 60px;
  display: inline-block;

  background-color: ${({ isClicked }) => (!isClicked ? "#EFEFEF" : "white")};
  border: ${({ isClicked }) =>
    !isClicked ? "0.2rem solid #EFEFEF" : "0.2rem solid #FF6262"};
  color: ${({ isClicked }) => (!isClicked ? "#B3B3B3" : "#FF6262")};
  font-weight: ${({ isClicked }) => (!isClicked ? 500 : 700)};
  box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.08);
`;

const HoverBox = styled.div`
  opacity: 0;
  position: absolute;
  border: 0.2rem solid #ff6262;
  margin-top: 1%;
  color: #ff6262;
  font-weight: 700;

  border-radius: 20px;
  padding: 0.5% 1%;
  margin-left: -1%;
  background-color: white;
  ${SelectBox}:hover & {
    opacity: 100%;
   
    transition: 0.5s ease-out;

  }
`;

const TotalBar = styled.div`
  text-align: center;
  margin: 3% auto;
  font-size: 1.1rem;
`;

const FirstBar = styled.div`
  background-color: #fff2f2;
  border-radius: 20px;
  padding: 5% 1%;
  margin-top: 4%;
`;

const SecondBar = styled.div`
  background-color: #fff2f2;
  border-radius: 20px;
  padding: 5% 1%;
  margin-top: 4%;
`;

const SelectBar = styled.div`
  text-align: center;
  margin: 3% auto;
  font-size: 1.2rem;
`;

const Bar = styled.div`
  display: flex;
  margin: 3% 3% 3% 3%;
`;

const BarText = styled.div`
  width: 30%;
  margin: 3% auto;
  font-size: 0.9rem;
`;

const animation = (w, e, bg, ex_bg) => keyframes` 


  0%{
    width: ${e}%;
    opacity:1;
    background-color: ${ex_bg};
  }
 
  100%{
    width: ${w}%;
    opacity:1;
    background-color: ${bg}
  }
`;

const MiniBar = styled.div`
  width: 60%;
  height: 20px;
  background-color: white;
  border-radius: 20px;

  overflow: hidden;
  margin: 3% auto;
`;
const Mini = styled.div`
  width: ${(props) => props.width}%;
  height: 30px;
  padding: 0;
  text-align: center;
  background-color: ${(props) => props.bgColor};
  color: #111;
  float: right;
  animation: ${(props) =>
    animation(props.width, props.ex_width, props.bgColor, props.ex_bg)};
  animation-fill-mode: none;
  animation-duration: 1s;
`;

const Save = styled.div`
  text-align: center;

  margin-top: 5%;
  padding: 4% 0;
  border-radius: 60px;
  background-color: #ff6262;
  color: white;
  font-weight: 700;
`;

const Next = styled.div`
  text-align: center;

  background-color: #fff2f2;
  padding: 4% 0;
  margin-top: 5%;
  border-radius: 60px;
  color: #ff6262;
  font-weight: 700;
`;



export default Selecttech;
