const SelectContainer = ({
  id,
  title,
  point,
  code,
  season_open,
  setSelect,
  select,
  ex_select,
  setSelectEX,
}) => {
  const [isClicked, setisClicked] = useState(false);

  const onClick = () => {
    setSelectEX(select);

    setisClicked(!isClicked);
    if (isClicked) {
      const filtered = select.filter(
        (item) => item[0] != id && item[1] != point
      );
   
      setSelect(filtered);
    } else {
      setSelect([...select, [id, point]]);
    }
  };

   if (item.season_open === true) {
     console.log("okay");
   }

  return (
    <>
      <SelectBox onClick={onClick} isClicked={isClicked}>
        {title}
        <HoverBox>
          과목 코드: {code}
          <br></br>학점: {point}학점
          <br></br>계절: {season_open}
        </HoverBox>
      </SelectBox>
    </>
  );
};

