import React from "react";
import styled from "styled-components";
import main_image from "./images/milestone.jpg";
import intro_image from "./images/intro_imag2.png";
import feature_image1 from "./images/main.jpeg";
import feature_image2 from "./images/main.jpeg";
import Feature from "./Feature";

const Introduce = () => {
  return (
    <>
      <MainSection>
        <MainImageWrapper>
          <MainImage src={main_image} alt="Main" />
        </MainImageWrapper>
        <MainText>RTS, Road Through Sogang</MainText>
        <SubText>당신을 도와줄 로드맵</SubText>
      </MainSection>
      <IntroSection>
        <IntroImageWrapper>
          <Image src={intro_image} alt="Intro" />
        </IntroImageWrapper>
        <IntroDescription>
          복수전공, 부전공... 학교 생활은 항상 선택의 연속입니다.
          <br />
          <span>RTS </span>는 그 선택을 조금 더 간편하게 도와주는
          <br />
          <span>로드맵 플랫폼 </span>
          입니다.
        </IntroDescription>
      </IntroSection>
      <FeatureSection>
        <FeatureTitle>Feature</FeatureTitle>
        <FeatureWrapper>
          <Feature
            image={feature_image1}
            description={{
              title: "로드맵 작성",
              detail:
                "신입생부터 재학생까지, 당신의 학기별 로드맵을 \n 직접 작성해보세요.",
            }}
          />
          <Feature
            image={feature_image2}
            description={{
              title: "학점 대시보드",
              detail:
                "이수 학점과 전공별로 학점을 얼만큼 채웠는지 \n 한눈에 볼수 있어요.",
            }}
          />
        </FeatureWrapper>
      </FeatureSection>
      <StartSection>
        <BeginPrompt>그럼, 시작해 볼까요 ?</BeginPrompt>
        <StyledButton>
          <span>시작하기</span>
        </StyledButton>
      </StartSection>
    </>
  );
};

const MainSection = styled.div`
  display: flex;
  height: 100vh;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  position: relative;
  width: 70%;
  margin: 0 auto;
`;

const MainText = styled.div`
  font-size: 4em;
  color: white;
  font-weight: bold;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
  position: absolute;
  top: 10%;
  left: 10%;
`;

const SubText = styled.div`
  font-size: 2.5em;
  color: white;
  font-weight: bold;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
  position: absolute;
  top: 40%;
  left: 10%;
`;

const MainImageWrapper = styled.div`
  width: 100%;
`;

const MainImage = styled.img`
  max-width: 100%;
  object-fit: cover;
`;
const IntroSection = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 70%;
  margin: 50px auto;
  border: 1px solid grey;

  @media (max-width: 768px) {
    flex-direction: column;
    width: 100%;
    padding: 40px 20px;
    justify-content: center;
  }
`;

const IntroImageWrapper = styled.div`
  flex: 1;
  margin-right: 10px;
`;

const Image = styled.img`
  max-width: 60%;
  object-fit: cover;
  opacity: 0.3;
`;

const IntroDescription = styled.div`
  flex: 1;
  font-size: 1.3em;
  position: relative;
  color: black;
  font-weight: bold;
  padding: 10px;
  border-radius: 5px;
  line-height: 3;
  span {
    color: #ff6262;
  }
`;
const FeatureTitle = styled.div`
  width: 100%;
  font-size: 2em;
  font-weight: bold;
  text-align: center;
  margin-top: 10px;
  margin-bottom: 10px;
`;
const FeatureWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
`;

const FeatureSection = styled.div`
  display: flex;
  flex-direction: column;
  width: 70%;
  margin: 50px auto;
  border: 1px solid grey;

  @media (max-width: 768px) {
    width: 100%;
    padding: 40px 20px;
  }
`;
const StartSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;
const BeginPrompt = styled.div`
  font-size: 1.3em;
  position: relative;
  color: black;
  font-weight: bold;
  padding: 10px;
  border-radius: 5px;
  line-height: 3;
`;

const StyledButton = styled.button`
  cursor: pointer;
  width: 30vw;
  height: 8vh;
  border-radius: 60px;
  border: 2px solid #ff6262;
  background: #ff6262;

  @media (max-width: 768px) {
    width: 50vw;
    height: 7vh;
  }
  span {
    color: #fff;
    text-align: center;
    font-family: Noto Sans;
    font-size: 1.3rem;
    font-style: normal;
    font-weight: 700;
    line-height: normal;
  }
`;
export default Introduce;
